// SignIn.js
import React, { useEffect, useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import "../style/SignIn.css";
import Footer from "../components/footer/Footer";
import { useDispatch, useSelector } from "react-redux";
import { loginUser } from "../store/actions/actions";

const SignIn = () => {
  const dispatch = useDispatch(); //renvoie action au store redux
  let navigate = useNavigate();

  //Stocke email et mot de passe
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const { error } = useSelector((state) => state.userLogin); //Extrais error a partir de redux
  const { token } = useSelector((state) => state.userLogin); //extrai le token a partir du store redux

  const handleSignIn = (e) => {
    e.preventDefault();
    dispatch(loginUser(email, password));
  };

  useEffect(() => {
    //Si token alors dispatch déclenche l'action
    if (token) {
      navigate("/sign-in");
    }
  }, [token, navigate]);

  return (
    <div>
      <NavLink to="/">
        <img
          className="main-logo-signin"
          src="./img/argentBankLogo.png"
          alt=""
        />
      </NavLink>
      <main className="main bg-dark">
        <section className="sign-in-content">
          <i className="fa fa-user-circle sign-in-icon"></i>
          <h1>Sign In</h1>
          <form onSubmit={handleSignIn}>
            <div className="input-wrapper">
              <label htmlFor="username">Username</label>
              <input
                type="text"
                id="username"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="input-wrapper">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="input-remember">
              <input type="checkbox" id="remember-me" />
              <label htmlFor="remember-me">Remember me</label>
            </div>
            <button className="sign-in-button" type="submit" name="Login">
              Sign In
            </button>
            {error && (
              <div>
                <br />
                {error}
              </div>
            )}
          </form>
        </section>
      </main>
      <Footer />
    </div>
  );
};

export default SignIn;
