// SignIn.js
import React, { useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import "../style/SignIn.css";
import Footer from "../components/footer/Footer";
import { useDispatch } from "react-redux";
import { loginUser, setUser } from "../store/actions/actions";

const SignIn = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [username, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);

  const handleSignIn = (e) => {
    e.preventDefault();

    // Make an API call to check user credentials
    axios
      .post("http://localhost:3000/api/v1/user/login", { username, password })
      .then((response) => {
        const matchedUser = response.data; // Assuming the API returns the user data if credentials are correct
        if (matchedUser) {
          dispatch(
            setUser({ name: matchedUser.username, email: matchedUser.email })
          );
          navigate("/user");
        } else {
          alert("Identifiant incorrect, veuillez réessayer !!");
        }
      })
      .catch((error) => {
        console.error("Error occurred while signing in:", error);
        alert(
          "Une erreur est survenue lors de la connexion, veuillez réessayer !!"
        );
      });

    // Clear form fields after submission
    setUserName("");
    setPassword("");
    setRememberMe(false);
  };

  return (
    <div>
      <NavLink to="/">
        <img
          className="main-logo-signin"
          src="./img/argentBankLogo.png"
          alt=""
        />
      </NavLink>
      <main className="main bg-dark">
        <section className="sign-in-content">
          <i className="fa fa-user-circle sign-in-icon"></i>
          <h1>Sign In</h1>
          <form onSubmit={handleSignIn}>
            <div className="input-wrapper">
              <label htmlFor="username">Username</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUserName(e.target.value)}
              />
            </div>
            <div className="input-wrapper">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="input-remember">
              <input
                type="checkbox"
                id="remember-me"
                value={rememberMe}
                onChange={(e) => setRememberMe(e.target.checked)}
              />
              <label htmlFor="remember-me">Remember me</label>
            </div>

            <button className="sign-in-button" type="submit">
              Sign In
            </button>
          </form>
        </section>
      </main>
      <Footer />
    </div>
  );
};

export default SignIn;
