import { useState, useEffect } from "react";

import { useDispatch, useSelector } from "react-redux";
import { login } from "../../store/";
import { useNavigate } from "react-router";

export default function SignInForm() {
  const dispatch = useDispatch(); //renvoie action au store redux
  let navigate = useNavigate();

  //Stocke email et mot de passe
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const { error } = useSelector((state) => state.userLogin); //Extrais error a partir de redux
  const { token } = useSelector((state) => state.userLogin); //extrai le token a partir du store redux

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(login(email, password));
  };

  useEffect(() => {
    //Si token alors dispatch déclenche l'action
    if (token) {
      navigate("/profile");
    }
  }, [token, navigate]);

  return (
    <section className="sign-in-content">
      <i className="fa fa-user-circle sign-in-icon"></i>
      <h1>Sign In</h1>
      <form onSubmit={submitHandler}>
        <div className="input-wrapper">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="input-wrapper">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="input-remember">
          <input type="checkbox" id="remember-me" />
          <label htmlFor="remember-me">Remember me</label>
        </div>
        <button className="sign-in-button" type="submit" name="Login">
          Sign In
        </button>
        {error && (
          <div>
            <br />
            {error}
          </div>
        )}
      </form>
    </section>
  );
}
