//Mise a jorus des userName
export const userName = (token, username) => {
  return (dispatch) => {
    axios
      .post(
        "http://localhost:3001/api/v1/user/profile",
        { username },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then((response) => {
        dispatch(updateUserNameSucces(response.data.userName));
      })
      .catch((error) => {
        dispatch(updateUserNameFailure(error.message));
      });
  };
};
