import axios from "axios";

export const SET_USER = "SET_USER";
export const EDIT_USER = "EDIT_USER";

export const setUser = (user) => ({
  type: SET_USER,
  payload: user,
});

export const editUser = (newName) => ({
  type: EDIT_USER,
  payload: newName,
});

export const loginUser = (credentials) => {
  return (dispatch) => {
    dispatch(setUser(credentials.username)); // Supposons que l'identifiant de l'utilisateur soit son nom d'utilisateur

    axios
      .post("http://localhost:3000/api/v1/user/login", credentials)
      .then((response) => {
        const user = response.data;
        if (user) {
          dispatch(editUser(user.name)); // Supposons que le nom d'utilisateur soit stocké dans la propriété "name" de la réponse
        } else {
          // Gérer le cas d'échec de connexion
        }
      })
      .catch((error) => {
        // Gérer les erreurs de requête
      });
  };
};
