import { SET_USER, EDIT_USER, USER_FAILURE_CONNECT } from "../actions/actions";

const initialState = {
  user: null,
  api: {
    loading: false,
    data: null,
    error: null,
  },
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_USER:
      return { ...state, api: { loading: true, data: null, error: null } };
    case EDIT_USER:
      return {
        ...state,
        user: action.payload,
        api: { loading: false, data: null, error: null },
      };
    default:
      return state;
  }
};

export default rootReducer;
