import {
  SET_USER_STARTED,
  SET_USER_SUCCES,
  SET_USER_FAILURE,
  SET_USER,
  UPDATE_USERNAME_SUCCESS,
  UPDATE_USERNAME_FAILURE,
  userName,
} from "../actions/actions";

const initialState = {
  user: null,
  api: {
    loading: false,
    data: null,
    error: null,
  },
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_USER_STARTED:
      return { ...state, api: { loading: true, data: null, error: null } };

    case SET_USER_SUCCESs:
      return {
        ...state,
        user: action.payload.user,
        token: action.payload.token,
        api: { loading: false, data: null, error: null },
      };

    case SET_USER_FAILURE:
      return {
        ...state,
        api: { loading: false, data: null, error: action.payload },
      };

    case SET_USER:
      return action.payload;

    case UPDATE_USERNAME_SUCCESS:
      return {
        ...state,
        user: {
          ...state.user,
          userName: action.payload,
        },
        api: { loading: false, data: null, error: null },
      };

    case UPDATE_USERNAME_FAILURE:
      return {
        ...state,
        api: {
          loading: false,
          data: null,
          error: action.payload,
        },
      };

    default:
      return state;
  }
};

export default rootReducer;
