import {
  SET_USER_STARTED,
  SET_USER_SUCCESS,
  SET_USER_FAILURE,
} from "../actions/actions";

const initialState = {
  user: null,
  api: {
    loading: false,
    data: null,
    error: null,
  },
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_USER_STARTED:
      return { ...state, api: { loading: true, data: null, error: null } };
  }
};
