import {
  SET_USER_STARTED,
  SET_USER_SUCCESS,
  SET_USER_FAILURE,
} from "../actions/actions";

const initialState = {
  user: {
    name: "",
    email: "",
  },
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_USER:
      return {
        ...state,
        user: action.payload,
      };
    case EDIT_USER:
      return {
        ...state,
        user: {
          ...state.user,
          name: action.payload,
        },
      };
    default:
      return state;
  }
};

export default userReducer;
