// SignIn.js
import React, { useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import "../style/SignIn.css";
import Footer from "../components/footer/Footer";
import { useDispatch, useSelector } from "react-redux";
import { loginUSer, setUser, updatePseudo } from "../store/actions/actions";

const SignIn = () => {
  const dispatch = useDispatch(); //Récupération de la fonction dispatch du store

  //States
  // const token = useSelector((state) => state.auth.token); //Stocke le token d'authentification du user

  //Stocke nom et password du user
  const [username, setUserName] = useState("");
  const [password, setPassword] = useState("");

  const [rememberMe, setRememberMe] = useState(false);

  //Hook
  const navigate = useNavigate();

  const handleSignIn = (e) => {
    e.preventDefault();

    console.log("Username", username);
    console.log("Password", password);

    dispatch(loginUSer({ email: username, password }));
    dispatch
    navigate("/user");

    // const settings = console.log("MatchUser:", matchedUser);

    // if (matchedUser) {
    //   dispatch(
    //     setUser({ name: matchedUser.username, email: matchedUser.email })
    //   );
    //   navigate("/user");
    // } else {
    //   alert("Identifiant incorrect, veuillez réessayer !!");
    // }

    //appel axios dans actions.js ensuite appel fonction dans signIn.js
  };

  return (
    <div>
      <NavLink to="/">
        <img
          className="main-logo-signin"
          src="./img/argentBankLogo.png"
          alt=""
        />
      </NavLink>
      <main className="main bg-dark">
        <section className="sign-in-content">
          <i className="fa fa-user-circle sign-in-icon"></i>
          <h1>Sign In</h1>
          <form onSubmit={handleSignIn}>
            <div className="input-wrapper">
              <label htmlFor="username">Username</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUserName(e.target.value)}
              />
            </div>
            <div className="input-wrapper">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="input-remember">
              <input
                type="checkbox"
                id="remember-me"
                value={rememberMe}
                onChange={(e) => setRememberMe(e.target.checked)}
              />
              <label htmlFor="remember-me">Remember me</label>
            </div>

            <button className="sign-in-button" type="submit">
              Sign In
            </button>
          </form>
        </section>
      </main>
      <Footer />
    </div>
  );
};

export default SignIn;
