export const SET_USER = "SET_USER";
export const EDIT_USER = "EDIT_USER";

export const setUser = (user) => ({
  type: SET_USER,
  payload: user,
});

export const editUser = (newName) => ({
  type: EDIT_USER,
  payload: newName,
});

//Action async pour effectuer la connexion
export const loginUser = (credentials) => {
  return (dispatch) => {
    dispatch(setUser(credentials.username));

    axios
      .post("http://localhost:3000/api/v1/user/login", credentials)
      .then((response) => {
        const user = response.data;
        if (user) {
          dispatch(loginRequestSuccess(user));
        } else {
          dispatch(loginRequestFailure("Invalid email or password"));
        }
      })
      .catch((error) => dispatch(loginRequestFailure(error)));
  };
};
