import axios from "axios";

export const SET_USER_STARTED = "SET_USER_STARTED";
export const SET_USER_SUCCESS = "SET_USER_SUCCESS";
export const SET_USER_FAILURE = "SET_USER_FAILURE";
export const SET_USER = "SET_USER";
export const UPDATE_USERNAME_SUCCESS = "UPDATE_USERNAME_SUCCESS";
export const UPDATE_USERNAME_FAILURE = "UPDATE_USERNAME_FAILURE";
// export const EDIT_USER = "EDIT_USER";

export const setUserStarted = () => ({
  type: SET_USER_STARTED,
});

export const setUserSuccess = (user, token) => ({
  type: SET_USER_SUCCESS,
  payload: { user: { ...user, userName: user.username }, token },
});

export const setUserFailure = (error) => ({
  type: SET_USER_FAILURE,
  payload: error,
});

export const setUser = (user) => ({
  type: SET_USER,
  payload: user,
});

export const updateUserNameSucces = (username) => ({
  type: UPDATE_USERNAME_SUCCESS,
  payload: userName,
});

export const updateUserNameFailure = (error) => ({
  type: UPDATE_USERNAME_FAILURE,
  payload: error,
});

//Action de connection
export const loginUSer = (credentials) => {
  return (dispatch) => {
    dispatch(setUserStarted());

    axios
      .post("http://localhost:3001/api/v1/user/login", credentials)
      .then((response) => {
        const user = response.data;
        if (user) {
          dispatch(setUserSuccess(user));
        } else {
          dispatch(setUserFailure("Email ou Mot de passe invalide"));
        }
      })
      .catch((error) => {
        setUserFailure(error);
      });
  };
};

//Mise a jorus des userName
export const userName = (token) => {
  return (dispatch) => {
    axios
      .get("http://localhost:3001/api/v1/user/profile", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        dispatch(updateUserNameSucces(response.data.userName));
      })
      .catch((error) => {
        dispatch(updateUserNameFailure(error.message));
      });
  };
};

