import React, { useState } from "react";
import { connect } from "react-redux";
import { editUser } from "../../store/actions/actions";
import "./EditName.css";

const EditName = ({ userName, editUser }) => {
  const [newName, setNewName] = useState("");
  // Fonction pr gérer changement de nom
  const handleChangeName = (e) => {
    setNewName(e.target.value);
  };

  // Fonction pr gérer soumission form
  const handleSubmit = (e) => {
    e.preventDefault();
    editUser(newName);
    setNewName("");
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter New Name"
          value={newName}
          onChange={handleChangeName}
        />
        <div></div>
        <button type="submit" className="container">
          Save
        </button>
        <button type="cancel" className="container">
          Cancel
        </button>
      </form>
    </div>
  );
};

// Fonction mapStateToProps pour mapper l'état du store Redux aux propriétés du composant + récup nom user dans store redux
const mapStateToProps = (state) => ({
  userName: state.user.name,
});

// Définition de l'objet mapDispatchToProps pour mapper l'action editUser aux propriétés du composant
const mapDispatchToProps = {
  editUser,
};

// Connecte le composant EditName au store Redux en utilisant connect
export default connect(mapStateToProps, mapDispatchToProps)(EditName);
