import { SET_USER, EDIT_USER, USER_FAILURE_CONNECT } from "../actions/actions";

const initialState = {
  user: null,
  api: {
    loading: false,
    data: null,
    error: null,
  },
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_USER:
      return {
        ...state,
        user: action.payload,
      };
    case EDIT_USER:
      return {
        ...state,
        user: {
          ...state.user,
          name: action.payload,
        },
      };
    default:
      return state;
  }
};

export default userReducer;
